body {
  background: radial-gradient(ellipse at top left, #1e1e2f 60%, #3a3a70 100%);
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
}

.glow-btn-container {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100vw;
  height: 100vh;
}

.glow-btn {
  position: relative;
  padding: 20px 50px;
  font-family: 'Poppins', sans-serif;
  font-size: 2rem;
  color: #fff;
  background: rgba(30,30,47,0.6);
  border-radius: 20px;
  border: none;
  outline: none;
  cursor: pointer;
  box-shadow: 0 4px 30px rgba(50,150,255,0.3);
  overflow: hidden;
  z-index: 1;
  transition: box-shadow 0.4s ease, background 0.4s ease;
}

.glow-btn::before {
  content: '';
  position: absolute;
  left: -50%;
  top: -50%;
  width: 200%;
  height: 200%;
  background: linear-gradient(135deg, #00ffea, #ff00c8, #fffb00, #00ffea);
  background-size: 400% 400%;
  border-radius: 40px;
  filter: blur(30px);
  opacity: 0.65;
  animation: glowing 4s infinite linear;
  z-index: -1;
}

@keyframes glowing {
  0% {background-position: 0% 50%;}
  50% {background-position: 100% 50%;}
  100% {background-position: 0% 50%;}
}

.glow-btn:hover {
  box-shadow: 0 0 80px #00ffea, 0 0 40px #ff00c8;
  background: rgba(30,30,47,0.85);
}
