.glow-btn {
  padding: 12px 32px;
  background: #111;
  color: #fff;
  border: 2px solid #0ff;
  border-radius: 12px;
  font-size: 18px;
  cursor: pointer;
  transition: transform .3s ease, box-shadow .3s ease;
  box-shadow: 0 0 10px #0ff;
}
.glow-btn:hover {
  box-shadow: 0 0 20px #0ff, 0 0 40px #0ff inset;
  transform: scale(1.05);
}

body {
    font-family: Arial, sans-serif;
    text-align: center;
    margin-top: 80px;
    background-color: #f2f2f2;
}

h1 {
    color: #333;
}

button {
    padding: 10px 20px;
    font-size: 16px;
    background: #007bff;
    color: white;
    border: none;
    border-radius: 6px;
    cursor: pointer;
}

button:hover {
    background: #0056b3;
}

.box {
  width: 100px;
  height: 100px;
  background: red;
  position: relative;
  animation: move 2s infinite alternate;
}

@keyframes move {
  from {
    left: 0;
  }
  to {
    left: 200px;
  }
}
